mapper.select:
SELECT COUNT(*)
  FROM tb_bookmark
 WHERE user_id = #{userId}
   AND hp_id = #{hpId}

mapper.delete:
DELETE
  FROM tb_bookmark
 WHERE user_id = #{userId}
   AND hp_id = #{hpId}

mapper.select:
SELECT b.hp_id , b.duty_name , b.duty_ha_yn , b.duty_er_yn , b.post_cdn1 , b.post_cdn2 , b.duty_addr , b.duty_tel , b.duty_inf FROM tb_bookmark a JOIN tb_emgc_rltm_master b ON a.hp_id = b.hp_id
AND a.user_id = #{userId}
AND b.duty_name LIKE CONCAT('%', #{dutyNm}, '%')
ORDER BY b.duty_name LIMIT #{offset}, #{size};

SELECT COUNT(*) FROM tb_bookmark a JOIN tb_emgc_rltm_master b ON a.hp_id = b.hp_id
AND a.user_id = #{userId}
AND b.duty_name LIKE CONCAT('%', #{dutyNm}, '%')
ORDER BY b.duty_name;
